/*================================
            CSS RESET
================================*/

html, body, div, span, applet, object, iframe,
h1, h2, h3, h4, h5, h6, p, blockquote, pre,
a, abbr, acronym, address, big, cite, code,
del, dfn, em, img, ins, kbd, q, s, samp,
small, strike, strong, sub, sup, tt, var,
b, u, i, center,
dl, dt, dd, ol, ul, li,
fieldset, form, label, legend,
table, caption, tbody, tfoot, thead, tr, th, td,
article, aside, canvas, details, embed, 
figure, figcaption, footer, header, hgroup, 
menu, nav, output, ruby, section, summary,
time, mark, audio, video {
	margin: 0;
	padding: 0;
	border: 0;
	font-size: 100%;
	font: inherit;
    vertical-align: baseline;
    text-decoration: none;
}
/* HTML5 display-role reset for older browsers */
article, aside, details, figcaption, figure, 
footer, header, hgroup, menu, nav, section {
	display: block;
}
body {
	line-height: 1;
}
ol, ul {
	list-style: none;
}
blockquote, q {
	quotes: none;
}
blockquote:before, blockquote:after,
q:before, q:after {
	content: '';
	content: none;
}
table {
	border-collapse: collapse;
	border-spacing: 0;
}

/*================================
            OWN CODE
================================*/

main {
    margin: 0 1em;
}

#pokemon > div:first-child {
}

#pokemon h1 {
    margin: 0;
    margin-top: 1em;
    margin-bottom: .5em;
    text-align: left;
}

section div p {
    margin-bottom: 1em;
}

h2 {
    font-size: 55px;
    font-weight: 700;
}

.no-display {
    display: none !important;
}

.types {
    display: block;
}

.types p {
    display: inline;
}

header nav ul {
    display: flex;
}

header nav ul li a {
    color: white;
    transition: all .4s ease;
    border-bottom: solid rgb(49, 49, 49) 1px;
    padding-bottom: .2em;
    font-size: 20px;
    margin-left: 2rem;
}

.pokemon::first-letter {
    text-transform: uppercase;
}

.pokemon {
    margin: .4em 0;
}

header nav ul li a:hover {
    border-bottom: solid rgb(255, 255, 255) 2px;
}

header nav {
    width: 30vw;
}

header {
    background-color: rgb(49, 49, 49);
    padding: 2em 0;
    width: 100vw;
    position: fixed;
    bottom: 0;
    z-index: 2;
}

h1,
h2,
h3,
h4,
h5,
h6,
p,
a,
li {
    font-family: sans-serif;
    color: rgb(49, 49, 49);
}

h1 {
    font-size: 80px;
    font-weight: 700;
    text-align: center;
    margin-bottom: 1em;
    margin-top: 15rem;
}

p {
    line-height: 1.3em;
}

.types,
.weight {
    margin-bottom: 1em;
    margin-top: .3em;
}

.name::first-letter {
    text-transform: uppercase;
}

.showPokemon {
    top: 0;
    transition: top .3s ease;
}

.pokemonlist {
    padding: 1em;
}

.template a {
    cursor: pointer;
    position: absolute;
    font-size: 1em;
    font-weight: 700;
    left: 1em;
    top: 4em;
}

.gone {
    display: none;
    visibility: hidden;
}

.loading,
.error {
    margin: 0 auto;
    display: block;
    font-size: 20px;
    margin-top: 2em;
}

.button {
    padding: .5em 1em;
    background-color:rgb(255, 255, 255);
    border: solid rgb(43, 43, 43) .2em;
    transition: 
        background-color .3s ease,
        color .3s ease;
    color: black;
}

.template {
    width: 30rem;
    margin: 0 auto;
    margin-top: 8rem;
}

.stats {
    float: right;
}

.sprites,
.sprites_shiny {
    display: flex;
    justify-content: space-around;
}

.sprites h2 {
    margin-bottom: 2rem;
}

.physical {
    float: left;
}

.template h2 {
    text-align: center;
}

.button:hover {
    background-color:rgb(43, 43, 43);
    color: rgb(255, 255, 255);
    cursor: pointer;
}

.error {
    display: none;
}

@keyframes visibility {
    0% { visibility: hidden; }
    100% { visibility: visible; }
}

@media screen and (min-width: 50rem) {
    main {
        margin: 0 2em;
    }

    header {
        padding: 1em 0;
        max-width: 100vw;
        position: inherit;
        position: fixed;
        top: 0;
        bottom: auto;
    }

    header nav ul li a {
        font-size: 16px;
    }
}
